<?phpnamespace App\Models;use Illuminate\Database\Eloquent\Model;use Illuminate\Database\Eloquent\Relations\BelongsTo;class OrderItem extends Model{    protected $fillable = [        'user_id',        'order_id',        'agent_id',        'service_id',        'service_quantity',        'price',        'expire_date',        'paid_status',        'expired_status',        'status',        'created_by',        'updated_by',    ];    public function order(): BelongsTo    {        return $this->belongsTo(Order::class);    }    protected function casts(): array    {        return [            'expire_date'    => 'date',            'paid_status'    => 'boolean',            'expired_status' => 'boolean',            'status'         => 'boolean',        ];    }}